// AI Agentの出力とWebhookデータを取得
const aiOutput = $('AI Agent').first().json.output;
const orderData = $('Webhook').first().json.body;

// コマンドタイプによる分岐
if (!orderData.command_type || orderData.command_type === 'new') {
  // 新規プロジェクトの場合（既存の動作を維持）
  const issueTitle = `${orderData.project_name} - AI自動開発案件`;
  const issueBody = `## プロジェクト発注情報

**発注日時**: ${new Date().toISOString()}
**プロジェクト名**: ${orderData.project_name}
**要件**: ${orderData.requirements}
**予算**: ${orderData.budget}
**期限**: ${orderData.deadline}
**優先度**: ${orderData.priority}

---

## AI分析結果

${aiOutput}

---

## 開発指示

@claude 上記の技術仕様書に基づいて、完全なプロジェクトを作成してください。

### 成果物要件:
1. 完全なソースコード
2. README.md（セットアップ手順含む）
3. 基本的なテストコード
4. package.json/requirements.txt（依存関係）
5. 基本的なAPI仕様書（該当する場合）

### 品質基準:
- コードは本番運用可能なレベル
- セキュリティベストプラクティスに従う
- レスポンシブデザイン対応（Webアプリの場合）
- 基本的なエラーハンドリング実装

### 技術スタック推奨:
AI分析結果の推奨技術スタックを参考にしてください。

完了時にはプルリクエストを作成し、デプロイ可能な状態で納品してください。`;

  return {
    json: {
      // GitHub API用の完全なリクエストボディ
      request_body: {
        title: issueTitle,
        body: issueBody,
        labels: ['ai-development', 'auto-order'],
        assignees: ['nappa0326']
      }
    }
  };

} else if (orderData.command_type === 'extend') {
  // 継続開発の場合
  // タスクタイプのマッピングをMapで定義（型安全）
  const taskTypeLabelMap = new Map([
    ['fix', 'バグ修正'],
    ['bugfix', 'バグ修正'],
    ['enhance', '機能追加'],
    ['refactor', 'リファクタリング'],
    ['update', '更新'],
    ['security', 'セキュリティ'],
    ['docs', 'ドキュメント'],
    ['test', 'テスト'],
    ['perf', 'パフォーマンス']
  ]);

  // task_typeを取得してラベルを決定
  const taskType = orderData.task_type || '';
  const taskLabel = taskTypeLabelMap.get(taskType) || taskType;

  const issueTitle = `[${taskLabel}] Issue #${orderData.original_issue_number} - ${orderData.description}`;

  // タスクタイプに応じた@claudeコマンドを生成
  const claudeCommand = taskType === 'bugfix' ?
    `@claude [bugfix: #${orderData.original_issue_number}] ${orderData.description}` :
    `@claude [extends: #${orderData.original_issue_number}] ${orderData.description}`;

  const issueBody = `## 作業内容
${orderData.description}

## 元のIssue
#${orderData.original_issue_number}

## タスクタイプ
${taskType} (${taskLabel})

## 優先度
${orderData.priority}

## 連絡先
${orderData.contact_info}

---

## AI分析結果

${aiOutput}

---

${claudeCommand}`;

  return {
    json: {
      // GitHub API用の完全なリクエストボディ
      request_body: {
        title: issueTitle,
        body: issueBody,
        labels: ['ai-development', 'auto-order', taskLabel, '継続開発'],
        assignees: ['nappa0326']
      }
    }
  };
}
