// PRã‚³ãƒ¡ãƒ³ãƒˆã®å†…å®¹ã‚’è§£æ
const eventType = $json.body.action; // created, edited, deleted
const comment = $json.body.comment?.body || '';
const prNumber = $json.body.issue?.number || $json.body.pull_request?.number;
const htmlUrl = $json.body.comment?.html_url || '';
const commenter = $json.body.comment?.user?.login || '';
const commentId = $json.body.comment?.id || null;
const prUrl = `https://github.com/${$json.body.repository?.full_name}/pull/${prNumber}`;
const issueNumber = $json.body.issue?.number || prNumber || 'N/A';
const issueTitle = $json.body.issue?.title || 'ã‚¿ã‚¤ãƒˆãƒ«ä¸æ˜';

// ãƒ–ãƒ©ãƒ³ãƒåã®æŠ½å‡ºï¼ˆClaude botã®ã‚³ãƒ¡ãƒ³ãƒˆã‹ã‚‰ï¼‰
let branchName = 'N/A';
const branchMatch = comment.match(/\[`([^`]+)`\]\(https:\/\/github\.com\/[^\/]+\/[^\/]+\/tree\/([^)]+)\)/);
if (branchMatch) {
  branchName = branchMatch[1] || branchMatch[2];
}

// Issue URLã®ç”Ÿæˆ
const issueUrl = `https://github.com/${$json.body.repository?.full_name}/issues/${issueNumber}`;

// @claude-review-neededã‚¿ã‚°ã®æ¤œå‡º
const hasReviewTag = comment.includes('@claude-review-needed');

// ãƒ•ã‚§ãƒ¼ã‚ºã®æ¤œå‡º
const phaseMatch = comment.match(/Phase (\d)/);
const phase = phaseMatch ? phaseMatch[1] : null;

// ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆå®Œäº†ãƒãƒ¼ã‚«ãƒ¼ã®æ¤œå‡ºï¼ˆä¿®æ­£ç‰ˆï¼‰
const hasProjectCompleteMarker =
    comment.includes('Phase 4') &&
    comment.includes('å®Œäº†');

// Claude Codeä½œæ¥­å®Œäº†ã®æ¤œå‡º
const isClaudeFinished =
    comment.startsWith('**Claude finished') &&
    comment.includes('task**') &&
    commenter === 'claude[bot]';

// é–‹ç™ºå®Œäº†åˆ¤å®š
const isProjectComplete = hasProjectCompleteMarker && commenter === 'claude[bot]';

// ãƒ¬ãƒ“ãƒ¥ãƒ¼ãŒå¿…è¦ã‹ã©ã†ã‹ã‚’åˆ¤å®š
const needsReview = hasReviewTag &&
  !isProjectComplete &&  // å®Œäº†æ™‚ã¯ãƒ¬ãƒ“ãƒ¥ãƒ¼ä¸è¦
  (eventType === 'created' ||
   (eventType === 'edited' && comment.includes('Claude finished')));

// é€šçŸ¥ã‚¿ã‚¤ãƒ—ã®æ±ºå®š
let notificationType = 'none';  // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆå€¤
if (hasProjectCompleteMarker && commenter === 'claude[bot]') {
  notificationType = 'completion';  // ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆå®Œäº†
} else if (needsReview) {
  notificationType = 'review';  // ãƒ¬ãƒ“ãƒ¥ãƒ¼é€šçŸ¥
}

return {
  json: {
    notification_type: notificationType,
    needs_review: needsReview,
    is_completed: isProjectComplete,
    pr_number: prNumber,
    issue_number: issueNumber,
    issue_title: issueTitle,
    issue_url: issueUrl,
    branch_name: branchName,
    phase: phase,
    comment: comment,
    html_url: htmlUrl,
    event_type: eventType,
    commenter: commenter,
    comment_id: commentId,
    pr_url: prUrl,
    has_phase4_marker: hasProjectCompleteMarker,
    is_claude_finished: isClaudeFinished
  }
};

