// å½¢å¼: project/{project-name}
let projectName = 'unknown';
const baseMatch = baseBranch.match(/^project\/(.+)$/);
if (baseMatch) {
  projectName = baseMatch[1];
}

// ãƒ˜ãƒƒãƒ‰ãƒ–ãƒ©ãƒ³ãƒã‹ã‚‰Issueç•ªå·ã¨ãƒ•ã‚§ãƒ¼ã‚ºã‚’æŠ½å‡º
// å½¢å¼: project/{project-name}-{issue-number}-phase{N}
let issueNumber = null;
